mapper.select:
SELECT RESULT.* FROM ( SELECT ROWNUM RN, T.* FROM ( SELECT O.*,S.STORE_NAME,C.* FROM ( SELECT * FROM ( SELECT COUNT(*) COUNT1 FROM ( SELECT delevery_status FROM bm_order_non_user UNION ALL SELECT delevery_status FROM bm_order_user ) WHERE delevery_status = '주문접수 대기 중' ), ( SELECT COUNT(*) COUNT2 FROM ( SELECT delevery_status FROM bm_order_non_user UNION ALL SELECT delevery_status FROM bm_order_user ) WHERE delevery_status = '처리 중' ) ) C ,( SELECT U.order_num, U.user_id, U.order_date, U.pay_method, U.delevery_status, U.phone, U.delevery_address1, U.delevery_address2, U.delevery_address3, UD.store_id, UD.amount, UD.used_point, UD.food_price, UD.total_price, UD.food_info, UD.request FROM bm_order_user U LEFT JOIN bm_order_detail_user UD ON U.ORDER_NUM = UD.ORDER_NUM
UNION ALL
SELECT NU.order_num, NU.user_id, NU.order_date, NU.pay_method, NU.delevery_status, NU.phone, NU.delevery_address1, NU.delevery_address2, NU.delevery_address3, NUD.store_id, NUD.amount, NUD.used_point, NUD.food_price, NUD.total_price, NUD.food_info, NUD.request FROM bm_order_non_user NU LEFT JOIN bm_order_detail_non_user NUD ON NU.ORDER_NUM = NUD.ORDER_NUM ) O LEFT JOIN BM_STORE S ON O.STORE_ID = S.ID WHERE DELEVERY_STATUS = #{list } ORDER BY ORDER_DATE ) T ) RESULT WHERE RN BETWEEN #{pageStart } AND #{pageEnd }

SELECT RESULT.* FROM ( SELECT ROWNUM RN, COUNT(*)OVER() LIST_COUNT, T.* FROM ( SELECT S.ID, S.CATEGORY, S.STORE_NAME, S.STORE_ADDRESS1, S.STORE_ADDRESS2, S.STORE_ADDRESS3 ,S.STORE_PHONE, S.STORE_IMG, S.STORE_THUMB, S.OPENING_TIME, S.CLOSING_TIME, S.MIN_DELEVERY, S.DELEVERY_TIME, S.DELEVERY_TIP, S.STORE_DES ,C.ORDER_COUNT, R.SCORE, R.REVIEW_COUNT, R.BOSS_COMMENT_COUNT FROM BM_STORE S LEFT JOIN ( SELECT store_id, ROUND(AVG(score),1) SCORE, count(review_content) review_count, count(boss_comment) boss_comment_count FROM bm_review GROUP BY store_id ) R ON S.ID = R.STORE_ID LEFT JOIN ( SELECT store_id, SUM(order_count) order_count from ( SELECT store_id, count(*) order_count FROM bm_order_detail_user GROUP BY store_id UNION ALL SELECT store_id, count(*) order_count FROM bm_order_detail_non_user GROUP BY store_id ) GROUP BY store_id ) C ON S.ID = C.STORE_ID ) T
WHERE store_name like '%${keyword}%'
) RESULT WHERE RN BETWEEN #{p.pageStart } AND #{p.pageEnd }

mapper.insert:
INSERT INTO bm_store (
    id,
    category,
    store_name,
    store_address1,
    store_address2,
    store_address3,
    store_phone,
    store_img,
    store_thumb,
    opening_time,
    closing_time,
    min_delevery,
    delevery_time,
    delevery_tip,
    store_des
) VALUES (
    STORE_ID_SEQ.NEXTVAL
    ,#{category }
    ,#{storeName }
    ,#{storeAddress1 }
    ,#{storeAddress2 }
    ,#{storeAddress3 }
    ,#{storePhone }
    ,#{storeImg }
    ,#{storeThumb }
    ,#{openingTime }
    ,#{closingTime }
    ,#{minDelevery }
    ,#{deleveryTime }
    ,#{deleveryTip }
    ,#{storeDes }
)

