	,STORE_ID
	,FOOD_NAME
	,FOOD_PRICE
	,FOOD_DEC
	,FOOD_IMG
	,FOOD_THUMB
) VALUES (
	#{id }
	,#{storeId }
	,#{foodName }
	,#{foodPrice }
	,#{foodDec }
	,#{foodImg }
	,#{foodThumb }
)

INSERT INTO BM_FOOD_OPTION (
	ID
	,FOOD_ID
	,OPTION_NAME
	,OPTION_PRICE
) VALUES (
	OPTION_ID_SEQ.NEXTVAL
	,#{foodId }
	,#{foodOption }
	,#{foodOptionPrice }
)

UPDATE BM_FOOD_OPTION SET
	OPTION_NAME = #{foodOption }
	,OPTION_PRICE = #{foodOptionPrice }
